import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function MatchDetail() {
  const { id } = useParams();
  const [match, setMatch] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/matches/${id}`);
        setMatch(response.data);
      } catch (error) {
        console.error('Error fetching match details:', error);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (!match) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Match Details</h2>
      <p><strong>Team 1:</strong> {match.team1.name}</p>
      <p><strong>Team 2:</strong> {match.team2.name}</p>
      <p><strong>Score:</strong> {match.scores.team1Score} - {match.scores.team2Score}</p>
      <h3>Player Statistics</h3>
      <ul>
        {Object.keys(match.stats).map(playerId => (
          <li key={playerId}>
            <p><strong>Player:</strong> {playerId}</p>
            <p><strong>Kills:</strong> {match.stats[playerId].kills}</p>
            <p><strong>Deaths:</strong> {match.stats[playerId].deaths}</p>
            <p><strong>Assists:</strong> {match.stats[playerId].assists}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MatchDetail;
