import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container, TextField, Button, Typography, Box, List, ListItem, ListItemText,
  IconButton, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
  MenuItem, Select, InputLabel, FormControl
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

function Players() {
  const [players, setPlayers] = useState([]);
  const [teams, setTeams] = useState([]);  // Lista de equipos
  const [name, setName] = useState('');
  const [team, setTeam] = useState('');
  const [editPlayer, setEditPlayer] = useState(null);
  const [deletePlayer, setDeletePlayer] = useState(null);

  useEffect(() => {
    fetchPlayers();
    fetchTeams();
  }, []);

  const fetchPlayers = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/players', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setPlayers(response.data);
    } catch (error) {
      console.error('Error fetching players:', error);
    }
  };

  const fetchTeams = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/teams', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editPlayer) {
      try {
        const response = await axios.put(`http://localhost:3000/api/players/${editPlayer._id}`, { name, team }, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        setPlayers(players.map((player) => (player._id === editPlayer._id ? response.data : player)));
        setEditPlayer(null);
        setName('');
        setTeam('');
      } catch (error) {
        console.error('Error updating player:', error);
      }
    } else {
      try {
        const response = await axios.post('http://localhost:3000/api/players', { name, team }, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        setPlayers([...players, response.data]);
        setName('');
        setTeam('');
      } catch (error) {
        console.error('Error creating player:', error);
      }
    }
  };

  const handleEdit = (player) => {
    setEditPlayer(player);
    setName(player.name);
    setTeam(player.team);
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:3000/api/players/${deletePlayer._id}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setPlayers(players.filter((player) => player._id !== deletePlayer._id));
      setDeletePlayer(null);
    } catch (error) {
      console.error('Error deleting player:', error);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Players</Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Player Name"
            fullWidth
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            margin="normal"
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Team</InputLabel>
            <Select
              value={team}
              onChange={(e) => setTeam(e.target.value)}
              required
            >
              {teams.map((team) => (
                <MenuItem key={team._id} value={team._id}>
                  {team.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            {editPlayer ? 'Update Player' : 'Add Player'}
          </Button>
        </form>
        <List sx={{ mt: 4 }}>
          {players.map((player) => (
            <ListItem key={player._id} secondaryAction={
              <>
                <IconButton edge="end" aria-label="edit" onClick={() => handleEdit(player)}>
                  <EditIcon />
                </IconButton>
                <IconButton edge="end" aria-label="delete" onClick={() => setDeletePlayer(player)}>
                  <DeleteIcon />
                </IconButton>
              </>
            }>
              <ListItemText primary={`${player.name} - ${player.team?.name || 'No Team'}`} />
            </ListItem>
          ))}
        </List>

        <Dialog open={!!deletePlayer} onClose={() => setDeletePlayer(null)}>
          <DialogTitle>Delete Player</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Are you sure you want to delete the player "{deletePlayer?.name}"?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeletePlayer(null)}>Cancel</Button>
            <Button onClick={handleDelete} color="error">Delete</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Players;
