import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Matches() {
  const [matches, setMatches] = useState([]);
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/matches');
      setMatches(response.data);
    } catch (error) {
      console.error('Error fetching matches:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/matches', { team1, team2 });
      setMatches([...matches, response.data]);
      setTeam1('');
      setTeam2('');
    } catch (error) {
      console.error('Error creating match:', error);
    }
  };

  return (
    <div>
      <h2>Matches</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Team 1 ID"
          value={team1}
          onChange={(e) => setTeam1(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Team 2 ID"
          value={team2}
          onChange={(e) => setTeam2(e.target.value)}
          required
        />
        <button type="submit">Add Match</button>
      </form>
      <ul>
        {matches.map((match) => (
          <li key={match._id}>{match.team1.name} vs {match.team2.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default Matches;
