import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import {
  Container, Typography, Box, List, ListItem, ListItemText
} from '@mui/material';

function MatchDetail() {
  const { id } = useParams();
  const [match, setMatch] = useState(null);

  useEffect(() => {
    fetchMatchDetails();
  }, []);

  const fetchMatchDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/matches/${id}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setMatch(response.data);
    } catch (error) {
      console.error('Error fetching match details:', error);
    }
  };

  if (!match) return <Typography>Loading...</Typography>;

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Match Details</Typography>
        <Typography variant="h6">{match.team1} vs {match.team2}</Typography>
        <Typography variant="h6">Score: {match.score1} - {match.score2}</Typography>
        <Typography variant="h5" sx={{ mt: 4 }}>Players' Stats</Typography>
        <List sx={{ mt: 4 }}>
          {Object.entries(match.stats).map(([playerId, stats]) => (
            <ListItem key={playerId}>
              <ListItemText primary={`Player ID: ${playerId}, Kills: ${stats.kills}, Deaths: ${stats.deaths}, Assists: ${stats.assists}`} />
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default MatchDetail;
