import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container, TextField, Button, Typography, Box, List, ListItem, ListItemText,
  IconButton, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
  Paper, Select, MenuItem, FormControl, InputLabel
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

function Tournaments() {
  const [tournaments, setTournaments] = useState([]);
  const [teams, setTeams] = useState([]);
  const [name, setName] = useState('');
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [editTournament, setEditTournament] = useState(null);
  const [deleteTournament, setDeleteTournament] = useState(null);

  useEffect(() => {
    fetchTournaments();
    fetchTeams();
  }, []);

  const fetchTournaments = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/tournaments', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setTournaments(response.data);
    } catch (error) {
      console.error('Error fetching tournaments:', error);
    }
  };

  const fetchTeams = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/teams', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const tournamentData = { name, teams: selectedTeams };
    if (editTournament) {
      try {
        await axios.put(`http://localhost:3000/api/tournaments/${editTournament._id}`, tournamentData, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        fetchTournaments();
        setEditTournament(null);
        setName('');
        setSelectedTeams([]);
      } catch (error) {
        console.error('Error updating tournament:', error);
      }
    } else {
      try {
        await axios.post('http://localhost:3000/api/tournaments', tournamentData, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        fetchTournaments();
        setName('');
        setSelectedTeams([]);
      } catch (error) {
        console.error('Error creating tournament:', error);
      }
    }
  };

  const handleEdit = (tournament) => {
    setEditTournament(tournament);
    setName(tournament.name);
    setSelectedTeams(tournament.teams.map(team => team._id));
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:3000/api/tournaments/${deleteTournament._id}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setTournaments(tournaments.filter((tournament) => tournament._id !== deleteTournament._id));
      setDeleteTournament(null);
    } catch (error) {
      console.error('Error deleting tournament:', error);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Tournaments</Typography>
        <Paper sx={{ p: 2 }}>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Tournament Name"
              fullWidth
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              margin="normal"
            />
            <FormControl fullWidth margin="normal" required>
              <InputLabel>Teams</InputLabel>
              <Select
                multiple
                value={selectedTeams}
                onChange={(e) => setSelectedTeams(e.target.value)}
              >
                {teams.map((team) => (
                  <MenuItem key={team._id} value={team._id}>{team.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
              {editTournament ? 'Update Tournament' : 'Add Tournament'}
            </Button>
          </form>
        </Paper>
        <List sx={{ mt: 4 }}>
          {tournaments.map((tournament) => (
            <Paper key={tournament._id} sx={{ mb: 2 }}>
              <ListItem secondaryAction={
                <>
                  <IconButton edge="end" aria-label="edit" onClick={() => handleEdit(tournament)}>
                    <EditIcon />
                  </IconButton>
                  <IconButton edge="end" aria-label="delete" onClick={() => setDeleteTournament(tournament)}>
                    <DeleteIcon />
                  </IconButton>
                </>
              }>
                <ListItemText
                  primary={tournament.name}
                  secondary={`Teams: ${tournament.teams.map(team => team.name).join(', ')}`}
                />
              </ListItem>
            </Paper>
          ))}
        </List>

        <Dialog open={!!deleteTournament} onClose={() => setDeleteTournament(null)}>
          <DialogTitle>Delete Tournament</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Are you sure you want to delete the tournament "{deleteTournament?.name}"?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteTournament(null)}>Cancel</Button>
            <Button onClick={handleDelete} color="error">Delete</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Tournaments;
