import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container, TextField, Button, Typography, Box, List, ListItem, ListItemText,
  IconButton, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
  Paper, Select, MenuItem, FormControl, InputLabel
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

function Matches() {
  const [matches, setMatches] = useState([]);
  const [teams, setTeams] = useState([]);
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [maps, setMaps] = useState([{ mapName: '', team1Score: '', team2Score: '', stats: [] }]);
  const [editMatch, setEditMatch] = useState(null);
  const [deleteMatch, setDeleteMatch] = useState(null);

  useEffect(() => {
    fetchMatches();
    fetchTeams();
  }, []);

  const fetchMatches = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/matches', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setMatches(response.data);
    } catch (error) {
      console.error('Error fetching matches:', error);
    }
  };

  const fetchTeams = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/teams', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  const handleMapChange = (index, field, value) => {
    const newMaps = [...maps];
    newMaps[index][field] = value;
    setMaps(newMaps);
  };

  const handleAddMap = () => {
    if (maps.length < 3) {
      setMaps([...maps, { mapName: '', team1Score: '', team2Score: '', stats: [] }]);
    }
  };

  const handleRemoveMap = (index) => {
    const newMaps = maps.filter((_, i) => i !== index);
    setMaps(newMaps);
  };

  const handlePlayerStatChange = (mapIndex, playerId, field, value) => {
    const newMaps = [...maps];
    const map = newMaps[mapIndex];
    const playerStat = map.stats.find(stat => stat.player === playerId);

    if (playerStat) {
      playerStat[field] = value;
    } else {
      map.stats.push({ player: playerId, [field]: value });
    }

    setMaps(newMaps);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const matchData = { team1, team2, maps };
    if (editMatch) {
      try {
        await axios.put(`http://localhost:3000/api/matches/${editMatch._id}`, matchData, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        fetchMatches();
        setEditMatch(null);
        setTeam1('');
        setTeam2('');
        setMaps([{ mapName: '', team1Score: '', team2Score: '', stats: [] }]);
      } catch (error) {
        console.error('Error updating match:', error);
      }
    } else {
      try {
        await axios.post('http://localhost:3000/api/matches', matchData, {
          headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
        });
        fetchMatches();
        setTeam1('');
        setTeam2('');
        setMaps([{ mapName: '', team1Score: '', team2Score: '', stats: [] }]);
      } catch (error) {
        console.error('Error creating match:', error);
      }
    }
  };

  const handleEdit = (match) => {
    setEditMatch(match);
    setTeam1(match.team1._id);
    setTeam2(match.team2._id);
    setMaps(match.maps.map((map) => ({
      mapName: map.mapName,
      team1Score: map.team1Score,
      team2Score: map.team2Score,
      stats: map.stats.map(stat => ({
        player: stat.player._id,
        kills: stat.kills,
        deaths: stat.deaths,
        assists: stat.assists
      }))
    })));
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:3000/api/matches/${deleteMatch._id}`, {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setMatches(matches.filter((match) => match._id !== deleteMatch._id));
      setDeleteMatch(null);
    } catch (error) {
      console.error('Error deleting match:', error);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Matches</Typography>
        <Paper sx={{ p: 2 }}>
          <form onSubmit={handleSubmit}>
            <FormControl fullWidth margin="normal" required>
              <InputLabel>Team 1</InputLabel>
              <Select
                value={team1}
                onChange={(e) => setTeam1(e.target.value)}
              >
                {teams.map((team) => (
                  <MenuItem key={team._id} value={team._id}>{team.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl fullWidth margin="normal" required>
              <InputLabel>Team 2</InputLabel>
              <Select
                value={team2}
                onChange={(e) => setTeam2(e.target.value)}
              >
                {teams.map((team) => (
                  <MenuItem key={team._id} value={team._id}>{team.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            {maps.map((map, index) => (
              <Box key={index} sx={{ mb: 2, p: 2, border: '1px solid #ccc', borderRadius: 1 }}>
                <TextField
                  label="Map Name"
                  fullWidth
                  value={map.mapName}
                  onChange={(e) => handleMapChange(index, 'mapName', e.target.value)}
                  required
                  margin="normal"
                />
                <TextField
                  label="Team 1 Score"
                  fullWidth
                  value={map.team1Score}
                  onChange={(e) => handleMapChange(index, 'team1Score', e.target.value)}
                  required
                  margin="normal"
                />
                <TextField
                  label="Team 2 Score"
                  fullWidth
                  value={map.team2Score}
                  onChange={(e) => handleMapChange(index, 'team2Score', e.target.value)}
                  required
                  margin="normal"
                />
                <Typography variant="h6" sx={{ mt: 2 }}>Player Stats</Typography>
                {[...teams.find(team => team._id === team1)?.players, ...teams.find(team => team._id === team2)?.players].map((player) => (
                  <Box key={player._id} sx={{ mt: 2 }}>
                    <Typography variant="subtitle1">{player.name}</Typography>
                    <TextField
                      label="Kills"
                      fullWidth
                      value={map.stats.find(stat => stat.player === player._id)?.kills || ''}
                      onChange={(e) => handlePlayerStatChange(index, player._id, 'kills', e.target.value)}
                      required
                      margin="normal"
                    />
                    <TextField
                      label="Deaths"
                      fullWidth
                      value={map.stats.find(stat => stat.player === player._id)?.deaths || ''}
                      onChange={(e) => handlePlayerStatChange(index, player._id, 'deaths', e.target.value)}
                      required
                      margin="normal"
                    />
                    <TextField
                      label="Assists"
                      fullWidth
                      value={map.stats.find(stat => stat.player === player._id)?.assists || ''}
                      onChange={(e) => handlePlayerStatChange(index, player._id, 'assists', e.target.value)}
                      required
                      margin="normal"
                    />
                  </Box>
                ))}
                {maps.length > 1 && (
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleRemoveMap(index)}
                    sx={{ mt: 1 }}
                  >
                    Remove Map
                  </Button>
                )}
              </Box>
            ))}
            {maps.length < 3 && (
              <Button
                variant="outlined"
                color="primary"
                onClick={handleAddMap}
                sx={{ mt: 2 }}
              >
                Add Map
              </Button>
            )}
            <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
              {editMatch ? 'Update Match' : 'Add Match'}
            </Button>
          </form>
        </Paper>
        <List sx={{ mt: 4 }}>
          {matches.map((match) => (
            <Paper key={match._id} sx={{ mb: 2 }}>
              <ListItem secondaryAction={
                <>
                  <IconButton edge="end" aria-label="edit" onClick={() => handleEdit(match)}>
                    <EditIcon />
                  </IconButton>
                  <IconButton edge="end" aria-label="delete" onClick={() => setDeleteMatch(match)}>
                    <DeleteIcon />
                  </IconButton>
                </>
              }>
                <ListItemText
                  primary={`${match.team1.name} vs ${match.team2.name}`}
                  secondary={`Winner: ${match.winner ? match.winner.name : 'TBD'}`}
                />
              </ListItem>
            </Paper>
          ))}
        </List>

        <Dialog open={!!deleteMatch} onClose={() => setDeleteMatch(null)}>
          <DialogTitle>Delete Match</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Are you sure you want to delete the match between "{deleteMatch?.team1.name}" and "{deleteMatch?.team2.name}"?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteMatch(null)}>Cancel</Button>
            <Button onClick={handleDelete} color="error">Delete</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Matches;
