import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container, Typography, Box, List, ListItem, ListItemText, Paper, Collapse, IconButton
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

function PlayerStats() {
  const [players, setPlayers] = useState([]);
  const [expanded, setExpanded] = useState({});

  useEffect(() => {
    fetchPlayers();
  }, []);

  const fetchPlayers = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/players', {
        headers: { Authorization: `Bearer ${sessionStorage.getItem('token')}` }
      });
      setPlayers(response.data);
      console.log('Fetched players:', response.data);  // Log player data
    } catch (error) {
      console.error('Error fetching players:', error);
    }
  };

  const handleExpandClick = (playerId) => {
    setExpanded((prevExpanded) => ({
      ...prevExpanded,
      [playerId]: !prevExpanded[playerId],
    }));
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Player Statistics</Typography>
        <List sx={{ mt: 4 }}>
          {players.map((player) => (
            <Paper key={player._id} sx={{ mb: 2 }}>
              <ListItem>
                <ListItemText
                  primary={player.name}
                  secondary={
                    <>
                      <Typography component="span" variant="body2" color="textPrimary">
                        Team: {player.team?.name || 'No Team'}
                      </Typography>
                      <Typography component="span" variant="body2" color="textPrimary" display="block">
                        Kills: {player.stats.kills} | Deaths: {player.stats.deaths} | Assists: {player.stats.assists}
                      </Typography>
                    </>
                  }
                />
                <IconButton edge="end" onClick={() => handleExpandClick(player._id)}>
                  {expanded[player._id] ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                </IconButton>
              </ListItem>
              <Collapse in={expanded[player._id]} timeout="auto" unmountOnExit>
                <Box sx={{ p: 2 }}>
                  <Typography variant="body2" color="textSecondary">Detailed Stats:</Typography>
                  {/* Aquí puedes agregar estadísticas detalladas */}
                </Box>
              </Collapse>
            </Paper>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default PlayerStats;
