import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box } from '@mui/material';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const { from } = location.state || { from: { pathname: "/dashboard" } };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Enviando solicitud de inicio de sesi贸n:', { email, password });
    try {
      const response = await axios.post('http://localhost:3000/api/users/login', { email, password });
      console.log('Respuesta del backend:', response.data);
      if (response.data.token) {
        sessionStorage.setItem('token', response.data.token);
        sessionStorage.setItem('role', response.data.role);  // Almacenar el rol
        navigate(from);
      } else {
        throw new Error('Token no recibido');
      }
    } catch (err) {
      console.error('Error en la solicitud de inicio de sesi贸n:', err.response || err.message);
      alert('Error en las credenciales');
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" gutterBottom>Login</Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Email"
            type="email"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            margin="normal"
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            margin="normal"
          />
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>Login</Button>
        </form>
      </Box>
    </Container>
  );
}

export default Login;
